Handlebars.registerHelper('countBadge', function(conditional, options) {
  if(conditional.count > 1) {
    return options.fn(this);
  } else {
    return options.inverse(this);
  }
});

Handlebars.registerHelper('promoTypeCheck', function(v1, promoType, options) {
   console.log(v1 + " " + promoType);
  if(v1 === promoType) {
    return options.fn(this);
  } else {
    return options.inverse(this);
  }
});

Handlebars.registerHelper('promoTypeCheckNot', function(v1, promoType, options) {
  if(v1 !== promoType) {
    return options.fn(this);
  }
});

Handlebars.registerHelper('renderRating', function(score) {
    var code = "";

    if (parseFloat(score) != 0) {

        var num = parseFloat(score);

        var s = num.toString();

        if (s.length == 1) {
            s += "00";
        }

        var ratingCSS = s.replace(".", "");

        code = '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    ';

    }

    return new Handlebars.SafeString(code);
});

