div.script:
var closePopup = function() {
    $("#checkin-form input").each(function() {
        if (!$(this).hasClass("noremove")) {
            $(this).val('');
        }
    });

    $("body").removeClass("bb_special_stick");

    if ($('.promo-area').is(":visible")) {
        if ($(".badge-area .badge-unlock").html().trim() !== '') {
            $(".stage").hide();
            $(".badge-area").show();
        } else {
            $(".stage").hide();
            $(".cheers-area").show();
        }
    } else {
        $(".checkin-modal-box").remove();
        $(".modal-overlay").remove();
    }
}

$(document).ready(function() {

    $(document).on("click", ".fb-badge-share", function(e) {

        var _user_badge_id = $(this).attr("data-user-badge-id");

        e.preventDefault();

                        var _url = 'https://untappd.com/b/'+_user_badge_id+'?utm_source=ut_fb_badge_web_post';

            if (window.logged_in_user_name) {
                _url = 'https://untappd.com/user/'+window.logged_in_user_name+'/badge/'+_user_badge_id+'?utm_source=ut_fb_badge_web_post';    
            }

            FB.ui({
              method: 'share',
              href: _url
            }, function(response){
                if (response) {
                   if (response.error_message) {
                        alert('We couldn\'t share to Facebook ('+response.error_message+')');
                    } else {
                        utCheckin.trackShare('badge-post', _user_badge_id, response.postId)  
                    } 
                }

            });



        return false;
    });

    $(document).on("click", ".subscribe-btn", function() {
        var _this = $(this);
        var venue_id = $(this).attr('data-venue-id');
        $(_this).html("Subscribed");
        $(_this).removeClass("blue").addClass('grey');

        var obj = "notification_type=1";

        $.ajax({
            url: "/apireqs/venuesubcribe/"+venue_id,
            dataType: "json",
            type: "POST",
            data: obj,
            error: function(html)
            {
                $(_this).html("Turn on Notification");
                $(_this).removeClass("grey").addClass('yellow');
            },
            success: function(html)
            {

            }
        });

        return false;
    })
})

$(document).ready(function() {
	// remove any query strings or access tokens
	if (history.replaceState) {
		if (location.href.indexOf("access_token=") >= 0) {
			history.replaceState(null, "", location.href.split("?")[0]);		
		}
	}
})

